import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  ButtonGroup,
  Button,
  CircularProgress,
  Paper,
} from '@mui/material';
import MediaOverview from '../components/MediaOverview';

const Dashboard = () => {
  const [mediaStats, setMediaStats] = useState(null);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Alle');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/stats');
        const data = await response.json();
        setMediaStats(data.media_stats);
        setCategories(['Alle', ...Object.keys(data.categories)]);
        setLoading(false);
      } catch (error) {
        console.error('Fejl ved hentning af statistik:', error);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const filteredMediaStats = selectedCategory === 'Alle'
    ? mediaStats
    : Object.fromEntries(
        Object.entries(mediaStats || {}).filter(
          ([_, stats]) => stats.category === selectedCategory
        )
      );

  if (loading) {
    return (
      <Box 
        sx={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          minHeight: '80vh' 
        }}
      >
        <CircularProgress size={60} thickness={4} />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 8 }}>
      <Paper 
        elevation={3} 
        sx={{ 
          p: 3,
          background: '#ffffff',
          borderRadius: 2,
        }}
      >
        <Box sx={{ mb: 4 }}>
          <ButtonGroup 
            variant="outlined" 
            aria-label="media category filter"
            sx={{
              display: 'flex',
              flexWrap: 'wrap',
              gap: 1,
              '& .MuiButton-root': {
                borderRadius: '20px',
                textTransform: 'none',
                px: 3,
              }
            }}
          >
            {categories.map((category) => (
              <Button
                key={category}
                onClick={() => setSelectedCategory(category)}
                variant={selectedCategory === category ? 'contained' : 'outlined'}
                sx={{
                  minWidth: 'auto',
                  mb: 1
                }}
              >
                {category}
              </Button>
            ))}
          </ButtonGroup>
        </Box>

        <MediaOverview mediaStats={filteredMediaStats} />
      </Paper>
    </Container>
  );
};

export default Dashboard;
