import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  ButtonGroup,
  Button,
  CircularProgress
} from '@mui/material';
import MediaOverview from '../components/MediaOverview';

const Dashboard = () => {
  const [mediaStats, setMediaStats] = useState(null);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Alle');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/stats');
        const data = await response.json();
        setMediaStats(data.media_stats);
        setCategories(['Alle', ...Object.keys(data.categories)]);
        setLoading(false);
      } catch (error) {
        console.error('Fejl ved hentning af statistik:', error);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const filteredMediaStats = selectedCategory === 'Alle'
    ? mediaStats
    : Object.fromEntries(
        Object.entries(mediaStats || {}).filter(
          ([_, stats]) => stats.category === selectedCategory
        )
      );

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Medieoversigt
      </Typography>

      <Box sx={{ mb: 3 }}>
        <ButtonGroup variant="outlined" aria-label="media category filter">
          {categories.map((category) => (
            <Button
              key={category}
              onClick={() => setSelectedCategory(category)}
              variant={selectedCategory === category ? 'contained' : 'outlined'}
            >
              {category}
            </Button>
          ))}
        </ButtonGroup>
      </Box>

      <MediaOverview mediaStats={filteredMediaStats} />
    </Container>
  );
};

export default Dashboard;
