import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  ButtonGroup,
  Button,
  CircularProgress,
  Paper,
  Grid,
  Card,
  CardContent,
} from '@mui/material';
import MediaOverview from '../components/MediaOverview';
import NewspaperIcon from '@mui/icons-material/Newspaper';
import CategoryIcon from '@mui/icons-material/Category';
import AnalyticsIcon from '@mui/icons-material/Analytics';

const StatCard = ({ icon, title, value }) => (
  <Card 
    elevation={3}
    sx={{
      height: '100%',
      background: 'linear-gradient(135deg, #1976d2 0%, #1565c0 100%)',
      color: 'white',
      transition: 'transform 0.2s',
      '&:hover': {
        transform: 'translateY(-4px)',
      }
    }}
  >
    <CardContent>
      <Box display="flex" alignItems="center" mb={2}>
        {icon}
        <Typography variant="h6" ml={1}>
          {title}
        </Typography>
      </Box>
      <Typography variant="h4" component="div">
        {value}
      </Typography>
    </CardContent>
  </Card>
);

const Dashboard = () => {
  const [mediaStats, setMediaStats] = useState(null);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Alle');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/stats');
        const data = await response.json();
        setMediaStats(data.media_stats);
        setCategories(['Alle', ...Object.keys(data.categories)]);
        setLoading(false);
      } catch (error) {
        console.error('Fejl ved hentning af statistik:', error);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const filteredMediaStats = selectedCategory === 'Alle'
    ? mediaStats
    : Object.fromEntries(
        Object.entries(mediaStats || {}).filter(
          ([_, stats]) => stats.category === selectedCategory
        )
      );

  if (loading) {
    return (
      <Box 
        sx={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          minHeight: '80vh' 
        }}
      >
        <CircularProgress size={60} thickness={4} />
      </Box>
    );
  }

  const totalArticles = Object.values(mediaStats || {}).reduce(
    (sum, stat) => sum + (stat.article_count || 0), 
    0
  );

  const totalCategories = categories.length - 1; // Minus 'Alle'

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 8 }}>
      <Typography 
        variant="h4" 
        gutterBottom 
        sx={{ 
          fontWeight: 700,
          mb: 4,
          textAlign: 'center',
          color: '#1976d2'
        }}
      >
        Medieoversigt
      </Typography>

      <Grid container spacing={4} sx={{ mb: 6 }}>
        <Grid item xs={12} md={4}>
          <StatCard 
            icon={<NewspaperIcon sx={{ fontSize: 40 }} />}
            title="Artikler analyseret"
            value={totalArticles.toLocaleString('da-DK')}
          />
        </Grid>
        <Grid item xs={12} md={4}>
          <StatCard 
            icon={<CategoryIcon sx={{ fontSize: 40 }} />}
            title="Mediekategorier"
            value={totalCategories}
          />
        </Grid>
        <Grid item xs={12} md={4}>
          <StatCard 
            icon={<AnalyticsIcon sx={{ fontSize: 40 }} />}
            title="Aktive medier"
            value={Object.keys(mediaStats || {}).length}
          />
        </Grid>
      </Grid>

      <Paper 
        elevation={3} 
        sx={{ 
          p: 3,
          background: '#ffffff',
          borderRadius: 2,
        }}
      >
        <Box sx={{ mb: 4 }}>
          <ButtonGroup 
            variant="outlined" 
            aria-label="media category filter"
            sx={{
              display: 'flex',
              flexWrap: 'wrap',
              gap: 1,
              '& .MuiButton-root': {
                borderRadius: '20px',
                textTransform: 'none',
                px: 3,
              }
            }}
          >
            {categories.map((category) => (
              <Button
                key={category}
                onClick={() => setSelectedCategory(category)}
                variant={selectedCategory === category ? 'contained' : 'outlined'}
                sx={{
                  minWidth: 'auto',
                  mb: 1
                }}
              >
                {category}
              </Button>
            ))}
          </ButtonGroup>
        </Box>

        <MediaOverview mediaStats={filteredMediaStats} />
      </Paper>
    </Container>
  );
};

export default Dashboard;
